import React from "react";
import "./Results.css";

function Results({ score, total, answers, questions, onRestart }) {
  const percentage = ((score / total) * 100).toFixed(2);

  return (
    <div className="results-container">
      <div className="results-card">
        <h1 className="results-title">üìä Exam Results</h1>

        <div className="score-box">
          <h2>Score: {score} / {total}</h2>
          <p>Percentage: {percentage}%</p>
        </div>

        <div className="review-section">
          <h3>üìù Review</h3>
          <table className="review-table">
            <thead>
              <tr>
                <th>#</th>
                <th>Question</th>
                <th>Your Answer</th>
                <th>Correct Answer</th>
                <th>Result</th>
              </tr>
            </thead>
            <tbody>
              {questions.map((q, i) => {
                const isCorrect = answers[i] === q.correctOption;
                return (
                  <tr key={i} className={isCorrect ? "correct-row" : "wrong-row"}>
                    <td>{i + 1}</td>
                    <td>{q.question}</td>
                    <td>{answers[i] || "Not Answered"}</td>
                    <td>{q.correctOption}</td>
                    <td>{isCorrect ? "‚úÖ" : "‚ùå"}</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>

        <button className="restart-btn" onClick={onRestart}>
          üîÑ Take Exam Again
        </button>
      </div>
    </div>
  );
}

export default Results;
