import React, { useState, useEffect } from 'react';
import "./Exam.css";
import { getQuestions, submitExam, getResult } from './api';

function Exam({ token }) {
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState([]);
  const [current, setCurrent] = useState(0);
  const [submitted, setSubmitted] = useState(false);
  const [result, setResult] = useState(null);
  const [timeLeft, setTimeLeft] = useState(30 * 60); // 30 minutes

  const shuffleArray = (arr) => {
    let newArr = [...arr];
    for (let i = newArr.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [newArr[i], newArr[j]] = [newArr[j], newArr[i]];
    }
    return newArr;
  };

  // Load questions on start
  useEffect(() => {
    const storedQuestions = localStorage.getItem("exam_questions");
    const storedAnswers = localStorage.getItem("exam_answers");
    const storedCurrent = localStorage.getItem("exam_current");
    const storedTimeLeft = localStorage.getItem("exam_timeLeft");

    if (storedQuestions && storedAnswers && storedCurrent && storedTimeLeft) {
      // Resume ongoing exam
      setQuestions(JSON.parse(storedQuestions));
      setAnswers(JSON.parse(storedAnswers));
      setCurrent(parseInt(storedCurrent, 10));
      setTimeLeft(parseInt(storedTimeLeft, 10));
    } else {
      // Fetch new questions
      getQuestions(token).then(res => {
        const shuffled = shuffleArray(res.data);
        setQuestions(shuffled);
        setAnswers(Array(shuffled.length).fill(null));
        localStorage.setItem("exam_questions", JSON.stringify(shuffled));
      });
    }
  }, [token]);

  // Save progress
  useEffect(() => {
    if (questions.length && !submitted) {
      localStorage.setItem("exam_questions", JSON.stringify(questions));
      localStorage.setItem("exam_answers", JSON.stringify(answers));
      localStorage.setItem("exam_current", current);
      localStorage.setItem("exam_timeLeft", timeLeft);
    }
  }, [questions, answers, current, timeLeft, submitted]);

  // Timer
  useEffect(() => {
    if (!questions.length || submitted) return;
    if (timeLeft <= 0) {
      handleSubmit();
      return;
    }
    const timer = setTimeout(() => setTimeLeft(t => t - 1), 1000);
    return () => clearTimeout(timer);
  }, [questions.length, submitted, timeLeft]);

  const handleOption = (idx) => {
    const newAnswers = [...answers];
    newAnswers[current] = idx;
    setAnswers(newAnswers);
  };

  const handleSubmit = () => {
    if (submitted) return;

    const payload = questions.map((q, i) => ({
      questionId: q._id,
      selectedOption: answers[i]
    }));

    submitExam(payload, token).then(() => {
      setSubmitted(true);
      localStorage.removeItem("exam_questions");
      localStorage.removeItem("exam_answers");
      localStorage.removeItem("exam_current");
      localStorage.removeItem("exam_timeLeft");

      // Fetch final result
      getResult(token).then(r => {
        setResult(r.data);
        window.location.hash = '#/results';
      });
    });
  };

  if (!questions.length && !submitted) {
    return (
      <div className="exam-container" style={{ textAlign: 'center', padding: '60px 0' }}>
        <h2>Loading questions...</h2>
      </div>
    );
  }

  if (submitted && result) {
    return (
      <div className="result-container card">
        <h2>Exam Submitted</h2>
        <div style={{ fontSize: '1.2em', margin: '12px 0' }}>
          <strong>Score:</strong> {result.score} / {result.review.length}
        </div>
        <button onClick={() => window.location.hash = '#/dashboard'}>Return to Dashboard</button>
      </div>
    );
  }

  const q = questions[current];
  const minutes = Math.floor(timeLeft / 60);
  const seconds = timeLeft % 60;

  return (
    <div className="exam-container">
      <div
        className="exam-timer"
        style={{ textAlign: 'right', fontWeight: '600', color: '#d32f2f', marginBottom: '16px', fontSize: '1.3em' }}
      >
        Time Left: {minutes.toString().padStart(2, '0')}:{seconds.toString().padStart(2, '0')}
      </div>

      <h3>Question {current + 1} of {questions.length}</h3>
      <div className="question" style={{ marginBottom: 16 }}>{q.question}</div>

      <ul style={{ listStyle: 'none', padding: 0 }}>
        {q.options.map((opt, idx) => (
          <li
            key={idx}
            style={{
              padding: '8px 12px',
              margin: '6px 0',
              border: '2px solid',
              borderColor: answers[current] === idx ? '#1976d2' : '#ccc',
              borderRadius: 6,
              backgroundColor: answers[current] === idx ? '#e3f2fd' : '#fff',
              cursor: 'pointer',
              fontWeight: answers[current] === idx ? 600 : 400
            }}
            onClick={() => handleOption(idx)}
          >
            <span style={{ fontWeight: 'bold', marginRight: 6 }}>
              {String.fromCharCode(65 + idx)}.
            </span>
            {opt}
          </li>
        ))}
      </ul>

      <div style={{ display: 'flex', gap: '16px', marginTop: '18px' }}>
        <button disabled={current === 0} onClick={() => setCurrent(c => c - 1)}>Previous</button>
        <button disabled={current === questions.length - 1} onClick={() => setCurrent(c => c + 1)}>Next</button>
        <button onClick={handleSubmit}>Submit Exam</button>
      </div>
    </div>
  );
}

export default Exam;
