import React, { useState, useEffect } from "react";
import Auth from "./Auth";
import Dashboard from "./Dashboard";
import Exam from "./Exam";
import Results from "./Results";

function App() {
  const [token, setToken] = useState(localStorage.getItem("token") || "");
  const [userId, setUserId] = useState(localStorage.getItem("userId") || "");
  const [page, setPage] = useState("login");

const handleSubmit = async () => {
  if (submitted) return;
  const payload = questions.map((q, i) => ({
    questionId: q._id,
    selectedOption: answers[i],
  }));

  try {
    await submitExam(payload, token);
    setSubmitted(true);

    // Clear exam storage
    localStorage.removeItem("exam_questions");
    localStorage.removeItem("exam_answers");
    localStorage.removeItem("exam_current");
    localStorage.removeItem("exam_timeLeft");

    // ✅ Mark that we are now viewing the results
    localStorage.setItem("viewing_results", "true");

    // Load results and go to results page
    const r = await getResult(token);
    setResult(r.data);
    window.location.hash = "#/results";
  } catch (err) {
    console.error(err);
    alert(err.response?.data?.message || "Error submitting exam");
  }
};

  const handleAuth = (newToken, newUserId) => {
    setToken(newToken);
    setUserId(newUserId);
    window.location.hash = "#/dashboard";
  };

  const handleLogout = () => {
    localStorage.clear();
    setToken("");
    setUserId("");
    window.location.hash = "#/login";
  };

  if (!token) return <Auth onAuth={handleAuth} />;

  switch (page) {
    case "dashboard":
      return <Dashboard onLogout={handleLogout} />;
    case "exam":
      return <Exam token={token} />;
    case "results":
      return <Results token={token} />;
    default:
      window.location.hash = "#/dashboard";
      return null;
  }
}

export default App;
