import React, { useState, useEffect, useCallback } from 'react';
import "./Exam.css";
import { getQuestions, submitExam, getResult } from './api';

function Exam({ token }) {
  const username = localStorage.getItem('username') || 'guest';
  const prefix = `exam_${username}_`;
  const KEY_START = `${prefix}start_time`;
  const KEY_CURRENT = `${prefix}current_index`;
  const KEY_ANSWERS = `${prefix}answers`;
  const KEY_SUBMITTED = `${prefix}submitted`;

  const EXAM_DURATION_MS = 30 * 60 * 1000; // 30 minutes

  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState([]);            // array of selected indices or null
  const [current, setCurrent] = useState(() => {
    const saved = parseInt(localStorage.getItem(KEY_CURRENT), 10);
    return !isNaN(saved) && saved >= 0 ? saved : 0;
  });
  const [timeLeft, setTimeLeft] = useState(() => {
    // We'll compute accurate remaining time from start_time on mount; start with fallback
    return Math.floor(EXAM_DURATION_MS / 1000);
  });
  const [submitted, setSubmitted] = useState(() => localStorage.getItem(KEY_SUBMITTED) === 'true');
  const [result, setResult] = useState(null);

  // Helper: clear saved exam state
  const clearSavedExam = () => {
    localStorage.removeItem(KEY_START);
    localStorage.removeItem(KEY_CURRENT);
    localStorage.removeItem(KEY_ANSWERS);
    localStorage.removeItem(KEY_SUBMITTED);
  };

  // Load questions and restore saved answers / current index and compute timeLeft from start_time
  useEffect(() => {
    let mounted = true;

    const load = async () => {
      // If already submitted (server), fetch result and bail out
      try {
        const r = await getResult(token);
        if (r?.data?.review && mounted) {
          setSubmitted(true);
          setResult(r.data);
          return;
        }
      } catch (err) {
        // ignore — no previous result
      }

      // Ensure start_time exists
      let start = localStorage.getItem(KEY_START);
      if (!start) {
        start = Date.now().toString();
        localStorage.setItem(KEY_START, start);
      }
      const startMs = parseInt(start, 10);
      const elapsed = Date.now() - startMs;
      const remaining = Math.max(Math.floor((EXAM_DURATION_MS - elapsed) / 1000), 0);
      if (mounted) setTimeLeft(remaining);

      // fetch questions
      try {
        const res = await getQuestions(token);
        if (!mounted) return;
        const qs = res?.data || [];
        setQuestions(qs);

        // restore saved answers (if present) and adapt to length
        let savedAnswers = null;
        try {
          savedAnswers = JSON.parse(localStorage.getItem(KEY_ANSWERS));
        } catch (e) {
          savedAnswers = null;
        }

        let answersToUse;
        if (Array.isArray(savedAnswers)) {
          if (savedAnswers.length === qs.length) answersToUse = savedAnswers;
          else if (savedAnswers.length < qs.length) answersToUse = [...savedAnswers, ...Array(qs.length - savedAnswers.length).fill(null)];
          else answersToUse = savedAnswers.slice(0, qs.length);
        } else {
          answersToUse = Array(qs.length).fill(null);
        }
        setAnswers(answersToUse);

        // restore current index but ensure it's in bounds
        const savedIndex = parseInt(localStorage.getItem(KEY_CURRENT), 10);
        if (!isNaN(savedIndex) && savedIndex >= 0 && savedIndex < qs.length) {
          setCurrent(savedIndex);
        } else {
          setCurrent(0);
        }
      } catch (err) {
        console.error("Failed to load questions:", err);
      }
    };

    load();
    return () => { mounted = false; };
  }, [token]); // run once on mount / token change

  // Persist answers and current index whenever they change
  useEffect(() => {
    try {
      localStorage.setItem(KEY_ANSWERS, JSON.stringify(answers));
    } catch (e) { /* ignore storage errors */ }
  }, [answers, KEY_ANSWERS]);

  useEffect(() => {
    localStorage.setItem(KEY_CURRENT, String(current));
  }, [current, KEY_CURRENT]);

  // Timer: compute remaining each second from start_time so refresh stays accurate
  const handleAutoSubmit = useCallback(async () => {
    // Double-check not already submitted
    if (submitted) return;
    try {
      const payload = questions.map((q, i) => ({
        questionId: q._id,
        selectedOption: answers[i] ?? null
      }));
      await submitExam(payload, token);
      localStorage.setItem(KEY_SUBMITTED, 'true');
      clearSavedExam();
      setSubmitted(true);
      const r = await getResult(token);
      setResult(r.data);
      // navigate to results page (optional)
      window.location.hash = '#/results';
    } catch (err) {
      console.error("Auto-submit error:", err);
    }
  }, [questions, answers, token, submitted, KEY_SUBMITTED]);

  useEffect(() => {
    if (!questions.length || submitted) return;
    let mounted = true;
    const tick = () => {
      const startS = localStorage.getItem(KEY_START);
      if (!startS) return;
      const startMs = parseInt(startS, 10);
      const elapsed = Date.now() - startMs;
      const remaining = Math.max(Math.floor((EXAM_DURATION_MS - elapsed) / 1000), 0);
      if (!mounted) return;
      setTimeLeft(remaining);
      if (remaining <= 0) {
        handleAutoSubmit();
      }
    };
    tick(); // run immediately
    const id = setInterval(tick, 1000);
    return () => { mounted = false; clearInterval(id); };
  }, [questions.length, submitted, handleAutoSubmit]);

  // Option click
  const handleOption = idx => {
    const newAnswers = Array.from(answers);
    newAnswers[current] = idx;
    setAnswers(newAnswers);
  };

  // Manual submit
  const handleSubmit = useCallback(async () => {
    if (submitted) return;
    try {
      const payload = questions.map((q, i) => ({
        questionId: q._id,
        selectedOption: answers[i] ?? null
      }));
      await submitExam(payload, token);
      localStorage.setItem(KEY_SUBMITTED, 'true');
      clearSavedExam();
      setSubmitted(true);
      const r = await getResult(token);
      setResult(r.data);
      window.location.hash = '#/results';
    } catch (err) {
      console.error("Submit error:", err);
    }
  }, [questions, answers, token, submitted]);

  // If already submitted & we have result -> show result block
  if (submitted && result) {
    const total = result.review?.length || 0;
    const correct = result.review?.filter(q => q.correct === q.selected).length || 0;
    const wrong = total - correct;
    const percent = total ? Math.round((correct / total) * 100) : 0;

    return (
      <div className="result-container card">
        <h2>Exam Submitted</h2>

        <div style={{ fontSize: '1.2em', margin: '12px 0', display: 'flex', justifyContent: 'space-between', maxWidth: 420 }}>
          <div><strong>Score:</strong> {result.score} / {total}</div>
          <div><strong>Percentage:</strong> {percent}%</div>
        </div>

        <div style={{ display: 'flex', gap: 20, marginBottom: 12 }}>
          <div style={{ color: '#43cea2', fontWeight: 'bold' }}><strong>Correct:</strong> {correct}</div>
          <div style={{ color: '#d32f2f', fontWeight: 'bold' }}><strong>Wrong:</strong> {wrong}</div>
        </div>

        <div>Submitted At: {new Date(result.submittedAt).toLocaleString()}</div>

        <h3 style={{ marginTop: 24 }}>Review Your Answers</h3>
        <div style={{ textAlign: 'left', margin: '0 auto', maxWidth: 700 }}>
          {result.review?.map((qItem, idx) => {
            const isCorrect = qItem.correct === qItem.selected;
            return (
              <div key={idx} style={{
                marginBottom: 24, padding: 16, borderRadius: 10, background: '#f7f7fa',
                boxShadow: '0 2px 8px rgba(0,0,0,0.04)'
              }}>
                <div style={{ fontWeight: 600, marginBottom: 8 }}>{idx + 1}. {qItem.question}</div>
                <div style={{ marginBottom: 8 }}>
                  <span style={{ color: isCorrect ? '#43cea2' : '#d32f2f', fontWeight: 600 }}>
                    {isCorrect ? 'Correct' : 'Wrong'}
                  </span>
                </div>
                <ul style={{ listStyle: 'none', padding: 0 }}>
                  {qItem.options.map((opt, i) => (
                    <li key={i} style={{
                      padding: '8px 12px',
                      margin: '6px 0',
                      border: '2px solid',
                      borderColor: qItem.correct === i ? '#43cea2' : (qItem.selected === i ? '#d32f2f' : '#ccc'),
                      borderRadius: 6,
                      backgroundColor: qItem.correct === i ? '#e8f5e9' : (qItem.selected === i && qItem.correct !== i ? '#ffebee' : '#fff'),
                      fontWeight: qItem.correct === i || qItem.selected === i ? 600 : 400
                    }}>
                      {String.fromCharCode(65 + i)}. {opt}
                      {qItem.correct === i ? ' (Correct)' : ''}
                      {qItem.selected === i && qItem.correct !== i ? ' (Your answer)' : ''}
                    </li>
                  ))}
                </ul>
              </div>
            );
          })}
        </div>

        <button onClick={() => window.location.hash = '#/dashboard'} style={{
          padding: '10px 16px', background: '#43cea2', color: '#fff', border: 'none', borderRadius: 6, cursor: 'pointer'
        }}>Return to Dashboard</button>
      </div>
    );
  }

  // Loading
  if (!questions.length) {
    return (
      <div className="exam-container" style={{ textAlign: 'center', padding: '60px 0' }}>
        <h2>Loading questions...</h2>
        <p style={{ marginTop: 12 }}>Preparing your exam…</p>
      </div>
    );
  }

  // Render current exam question
  const q = questions[current];
  const minutes = Math.floor(timeLeft / 60);
  const seconds = timeLeft % 60;

  return (
    <div className="exam-container">
      <div className="exam-timer" style={{
        textAlign: 'right', fontWeight: 600, color: '#d32f2f', marginBottom: 16, fontSize: '1.2em'
      }}>
        Time Left: {minutes.toString().padStart(2, '0')}:{seconds.toString().padStart(2, '0')}
      </div>

      <h3>Question {current + 1} of {questions.length}</h3>
      <div className="question" style={{ marginBottom: 16 }}>{q.question}</div>

      <ul style={{ listStyle: 'none', padding: 0 }}>
        {q.options.map((opt, idx) => (
          <li key={idx}
            onClick={() => handleOption(idx)}
            style={{
              padding: '10px 14px',
              margin: '8px 0',
              border: '2px solid',
              borderColor: answers[current] === idx ? '#1976d2' : '#ccc',
              borderRadius: 8,
              backgroundColor: answers[current] === idx ? '#e3f2fd' : '#fff',
              cursor: 'pointer',
              fontWeight: answers[current] === idx ? 700 : 500
            }}>
            <span style={{ fontWeight: 800, marginRight: 8 }}>{String.fromCharCode(65 + idx)}.</span> {opt}
          </li>
        ))}
      </ul>

      <div style={{ display: 'flex', gap: 12, marginTop: 18 }}>
        <button disabled={current === 0} onClick={() => setCurrent(c => Math.max(0, c - 1))}>Previous</button>
        <button disabled={current === questions.length - 1} onClick={() => setCurrent(c => Math.min(questions.length - 1, c + 1))}>Next</button>
        <button onClick={handleSubmit} style={{ marginLeft: 'auto', background: '#1976d2', color: '#fff', border: 'none', padding: '8px 12px', borderRadius: 6 }}>Submit Exam</button>
      </div>
    </div>
  );
}

export default Exam;
