// export default Exam;


import React, { useState, useEffect } from "react";
import { getQuestions, submitExam, getResult } from "./api";
import "./Exam.css";

function Exam({ token }) {
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState([]);
  const [current, setCurrent] = useState(0);
  const [submitted, setSubmitted] = useState(false);
  const [result, setResult] = useState(null);
  const [timeLeft, setTimeLeft] = useState(30 * 60);

  useEffect(() => {
    getResult(token)
      .then((r) => {
        if (r.data && r.data.review) {
          setSubmitted(true);
          setResult(r.data);
        } else {
          getQuestions(token).then((res) => {
            setQuestions(res.data);
            setAnswers(Array(res.data.length).fill(null));
          });
        }
      })
      .catch(() => console.error("Error fetching exam"));
  }, [token]);

  useEffect(() => {
    if (!questions.length || submitted) return;
    if (timeLeft <= 0) {
      handleSubmit();
      return;
    }
    const timer = setTimeout(() => setTimeLeft((t) => t - 1), 1000);
    return () => clearTimeout(timer);
  }, [timeLeft, questions.length, submitted]);

  const handleOption = (idx) => {
    const newAnswers = [...answers];
    newAnswers[current] = idx;
    setAnswers(newAnswers);
  };

  const handleSubmit = () => {
    if (submitted) return;
    const payload = questions.map((q, i) => ({
      questionId: q._id,
      selectedOption: answers[i] !== null ? answers[i] : null
    }));

    submitExam(payload, token)
      .then((r) => {
        setSubmitted(true);
        setResult(r.data);
        localStorage.removeItem("exam_questions");
        localStorage.removeItem("exam_answers");
        window.location.hash = "#/results";
      })
      .catch((err) => {
        console.error(err.response?.data || err.message);
        alert("Error submitting exam. Please try again.");
      });
  };

  if (!questions.length && !submitted) return <h2>Loading questions...</h2>;
  if (submitted && result)
    return (
      <div>
        <h2>Exam Submitted</h2>
        <p>
          Score: {result.score} / {result.review.length}
        </p>
        <button onClick={() => (window.location.hash = "#/dashboard")}>
          Return to Dashboard
        </button>
      </div>
    );

  const q = questions[current];
  const minutes = Math.floor(timeLeft / 60);
  const seconds = timeLeft % 60;

  return (
    <div className="exam-container">
      <div>
        Time Left: {minutes}:{seconds.toString().padStart(2, "0")}
      </div>
      <h3>
        Question {current + 1} of {questions.length}
      </h3>
      <p>{q.question}</p>
      <ul>
        {q.options.map((opt, idx) => (
          <li
            key={idx}
            style={{
              fontWeight: answers[current] === idx ? 600 : 400,
              cursor: "pointer"
            }}
            onClick={() => handleOption(idx)}
          >
            {String.fromCharCode(65 + idx)}. {opt}
          </li>
        ))}
      </ul>
      <button disabled={current === 0} onClick={() => setCurrent((c) => c - 1)}>
        Previous
      </button>
      <button
        disabled={current === questions.length - 1}
        onClick={() => setCurrent((c) => c + 1)}
      >
        Next
      </button>
      <button onClick={handleSubmit}>Submit Exam</button>
    </div>
  );
}

export default Exam;
